INSERT INTO `documents` (`id`, `title`, `content`, `perex`, `user_id`, `published`, `ord`, `slug`) VALUES
(1,	'První zápas Kiki po rozchodu s Maki? Již v pátek v Beachklubu Ládví!',	'<p>Pražský Beachklub Ládví coby spolupořadatel prestižního turnaje s mezinárodní účastí<strong>&nbsp;Staropramen Cool Super Cup</strong>, kterého se účastní největší hvězdy českého&nbsp;plážového volejbalu včetně olympioniků&nbsp;<strong>Přemysla Kubaly, Lenky Háječkové, Soni Novákové - Dosoudilové a Kristýny Kolocové</strong>, bude dějištěm velké sportovní show. A právě na poslední jmenovanou,&nbsp;<strong>Kiki</strong>, bude směřovat veškerá pozornost, protože po rozchodu s Maki (Markétou Slukovou) se poprvé představí s novou partnerkou, taktéž účastnicí olympijských her v Londýně, Hankou Skalníkovou (roz. Klapalovou).</p><p><img src=\"/files/gallery/3/docs-0feaa0f9d07d7538435e9f146cbb2d48.jpg\" width=\"570\"></p><p>Jako druhý nasazený pár turnaje nastoupí dvojice Kolocová - Skalníková do hlavní soutěže v&nbsp;<strong>pátek 14. srpna</strong>&nbsp;odpoledne, konkrétně v&nbsp;<strong>17:20</strong>. Věříme, že nás čeká velmi atraktivní utkání!</p><p>Těšíme se na vás.</p><p>&nbsp;</p><p><br>Beachklub Ládví</p>',	'<p>Pražský Beachklub Ládví coby spolupořadatel prestižního turnaje s mezinárodní účastí<strong>&nbsp;Staropramen Cool Super Cup</strong>, kterého se účastní největší hvězdy českého&nbsp;plážového volejbalu včetně olympioniků&nbsp;<strong>Přemysla Kubaly, Lenky Háječkové, Soni Novákové - Dosoudilové a Kristýny Kolocové</strong>, bude dějištěm velké sportovní show. A právě na poslední jmenovanou,&nbsp;<strong>Kiki</strong>, bude směřovat veškerá pozornost, protože po rozchodu s Maki (Markétou Slukovou) se poprvé představí s novou partnerkou, taktéž účastnicí olympijských her v Londýně, Hankou Skalníkovou (roz. Klapalovou). Více informací&nbsp;<a href=\"/prvni-zapas-kiki-po-rozchodu-s-maki\">zde</a>.</p><p><img src=\"/files/gallery/3/docs-4e3b7b637c22bc5b46a958ebf61869c7.jpg\" width=\"570\"></p>',	2,	1,	1,	'prvni-zapas-kiki-po-rozchodu-s-maki'),
(2,	'Vyhraje Kiki svůj první turnaj v nově složené dvojici s Hanou Skalníkovou?',	'<h3>Beachvolejbalová sezóna je v plném proudu a letošní léto je v Česku ve znamení špičkového plážového volejbalu. Již tento víkend 14. – 16. srpna 2015 se odehraje nejprestižnější turnaj „Staropramen Cool Super Cup 2015“ v Praze v areálu Beachklubu Ládví a na Císařské louce. Bude to první turnaj, na kterém se ukáže Kristýna Kolocová po rozpadu páru Maki a Kiki…</h3><p><img src=\"/files/gallery/3/docs-b8afe89df2f50a41071837da57b0b5d4.jpg\" width=\"570\"></p><p>Kromě Kristýny Kolocové se představí i olympionička Lenka Háječková se Soňou Novákovou - Dosoudilovou, Diana Žolnerčíková, Martina Jakubšová, Eliška Gálová, Šárka Nakládalová, nebo Martina Bonnerová, v mužské kategorii se můžeme těšit na bronzové medailisty z Evropských her v Baku Jana Hadravu a olympionika Přemysla Kubalu, Roberta Kufu, Jana Dumka a další.</p><p>Systém turnaje je dvoudenní s kvalifikací před turnajem. V hlavní soutěži se přestaví 16 párů, jsou připraveny 2 divoké karty pro zahraniční dvojice a 1 divoká karta pro zajímavou českou dvojici. Diváci tak uvidí 9 nejlepších dvojic aktuálního českého žebříčku a zbylé páry doplní 4 postupující z kvalifikace.</p><p>Finálové zápasy obou kategorií budou za účasti kamer ČT4 Sport, a tak budou mít možnost i diváci, kteří se nedostanou na turnaj, sledovat ten nejlepší beachvolejbal v České republice.</p><p>Na turnaji Super Cup jako turnaji nejvyšší kategorie jsou připraveny prize money ve výši 100.000,- Kč na kategorii. Tuto částku si rozdělí 12 nejlepších dvojic z hlavní soutěže.</p><p><strong>Harmonogram turnaje:</strong></p><p>14. – 15. 8. 2015&nbsp;kvalifikace a hlavní soutěže (Beachklub Ládví)<br>16. 8. 2015&nbsp;&nbsp;semifinále a finále (Císařská louka)</p><p>Diváky v Beachklubu Ládví čeká kromě profesionálních sportovních výkonů bohatý doprovodný program. Rodiny s dětmi uvítají dětský koutek s nafukovacími atrakcemi a na své si přijdou i labužníci, kteří ocení dobroty z venkovního grilu v bezprostřední blízkosti centrkurtu.</p><p><em>\"Jsem nesmírně ráda, že je to po roce opět Městská část Praha 8, která v areálu Beachklubu Ládví hostí tak významnou sportovní událost, kterou Staropramen Cool Super Cup bezpochyby je,\"</em>&nbsp;zve na beachvolejbalovou událost léta místostarostka paní Alena Borhyová.</p><p>Partnery turnaje jsou Staropramen Cool, Český volejbalový svaz, Hlavní město Praha, Městská část Praha 8, Oakley, Neumann, Metaxa, Monstera International, ITS Billa Travel, Adidas, Happy Plugs a E.On.</p><p>Doražte, těšíme se na vás!</p><p>&nbsp;</p><p>Beachklub Ládví</p>',	'<p>Beachvolejbalová sezóna je v plném proudu a letošní léto je v Česku ve znamení špičkového plážového volejbalu. Již tento víkend 14. – 16. srpna 2015 se odehraje nejprestižnější turnaj „Staropramen Cool Super Cup 2015“ v Praze v areálu Beachklubu Ládví a na Císařské louce. Bude to první turnaj, na kterém se ukáže Kristýna Kolocová po rozpadu páru Maki a Kiki… Více informací o celém turnaji naleznete v článku&nbsp;<a href=\"/vyhraje-kiki-svuj-prvni-turnaj\">zde</a>.</p><h3><img src=\"/files/gallery/3/docs-4605e06484fadd2af769d75c338d96ee.jpg\" width=\"570\"></h3>',	2,	1,	0,	'vyhraje-kiki-svuj-prvni-turnaj'),
(4,	'Beachvolejbalové kempy a kroužky',	'<h3>ONLINE PŘIHLAŠOVÁNÍ NA KEMPY, KROUŽKY A TRÉNINKY ZDE:</h3>\r\n<p><a href=\"/krouzky\">Pro vstup do&nbsp;<strong>online rezervace&nbsp;</strong>kempů, kroužků a tréninků klikněte&nbsp;<strong>zde</strong>. Otevře se vám nové okno. Pokud nemáte jméno a heslo, tak se musíte registrovat, poté se už můžete online registrovat.</a></p>\r\n<h3>Na léto pro vás připravujeme:</h3>\r\n<p>Na léto 2015 opět připravujeme oblíbené prázdninové kempy pro děti, kempy u moře nebo v Česku mimo areál Beachklubu Ládví.</p>\r\n<p><a href=\"kempyvcechach\"><span>Prázdninové kempy pro děti</span></a></p>\r\n<p><a href=\"kempyvcechach\"><span>Prodloužené víkendy v Poděbradech</span></a></p>\r\n<p><a href=\"kempyvcechach\">Beachvolejbalové kempy u Máchova jezera</a></p>\r\n<p><a href=\"kempyvcechach\"><span>Kempy na Ládví</span></a></p>\r\n<p><a title=\"beachkempy u moře\" href=\"kempyvitalii\"><span>Kempy v Itálii u moře</span></a></p>\r\n<h3>Přehled beach volejbalových kempů a kroužků:</h3>\r\n<p>Přes zimu probíhají kempy a kroužky v největší hale na plážový volejbal v Praze. Vytápěná nafukovačka ukrývá hned 6 kurtů na plážový volejbal.</p>\r\n<p><a href=\"deti-a-mladez\">Děti a mládež</a></p>\r\n<p><a href=\"maminky\">Maminky s dětmi</a></p>\r\n<p><a href=\"dospeli\">Dospělí</a></p>\r\n<p>V případě zájmu pište prosím našemu&nbsp;<a href=\"/o-nas\">šéftrenérovi</a>&nbsp;na e-mail<a href=\"mailto:seftrener@beachklubladvi.cz\">seftrener@beachklubladvi.cz</a>&nbsp;nebo volejte na telefon 605 218 056.</p>',	'<p><br></p>',	2,	0,	0,	'krouzky'),
(5,	'Rezervace beach volejbalových kurtů',	'<h3>Rezervace kurtů: &nbsp;<span></span></h3><h3>POZOR! Nový rezervační systém pro kurty vyžaduje&nbsp;<span>novou registraci</span>!</h3><h3>Nový rezervační systém je odlišný od předchozího, který zůstal zachován pro tréninky, turnaje a kempy!</h3><p><a href=\"https://jdemenato.cz/reservation/beachklub-ladvi/reservationcalendaroverview\" target=\"_blank\">Pro vstup do&nbsp;<strong>online rezervace&nbsp;</strong>kurtů&nbsp;</a><a href=\"https://jdemenato.cz/reservation/beachklub-ladvi/reservationcalendaroverview\" target=\"_blank\">klikněte&nbsp;</a><a href=\"/rezervace-kurtu\" target=\"_blank\"><strong>zde</strong>. Otevře se vám nové okno.<strong>Pokud nemáte jméno a heslo, tak se musíte registrovat, poté už můžete online rezervovat volné kurty</strong>.</a></p><br>',	'<p><br></p>',	2,	0,	0,	'rezervace-kurtu'),
(6,	'Beachvolejbalové turnaje v Praze v Beachklubu Ládví',	'<h3>ONLINE PŘIHLAŠOVÁNÍ NA TURNAJE ZDE:<br></h3><p><a href=\"/turnaje-v-praze\">Pro vstup do&nbsp;<strong>online rezervace&nbsp;</strong>turnajů klikněte&nbsp;<strong>zde</strong>. Otevře se vám nové okno. Pokud nemáte jméno a heslo, tak se musíte registrovat, poté se už můžete online registrovat na turnaj.</a></p><p><span style=\"font-family: BenchNine, sans-serif; font-size: 20px; line-height: 26px;\">TURNAJE V&nbsp;BEACHKLUBU LÁDVÍ PRO ZIMNÍ SEZÓNU 2014/2015</span><br></p><p><span>Pro<strong>zimní beachvolejbalovou sezónu 2014/2015</strong>&nbsp;jsme si pro Vás připravili hned několik zajímavých turnajů. Pro usnadnění Vaší orientace budeme pořádat beachvolejbalové turnaje konkrétní herní úrovně vždy stejný víkend v&nbsp;měsíci.</span></p><p><strong><span>První víkend</span></strong><span>&nbsp;v&nbsp;měsíci bude sobota věnována&nbsp;<strong>Juniorlize Beachklubu Ládví</strong>(v čase od 9:00 do 14:00 hod.). Sobotní odpoledne může být využito na soukromé/firemní akce. V&nbsp;neděli pak můžete dorazit na&nbsp;<strong>mixový amatérský turnaj MIRACUP pro kempaře</strong>&nbsp;organizátora Míry Justiána.</span></p><p><strong><span>Druhý víkend</span></strong><span>&nbsp;v&nbsp;měsíci startují&nbsp;<strong>otevřené turnaje PLAY HARD! TOUR</strong>organizátorky Barči Skoumalové. Sobota je věnována otevřeným turnajům mužů a žen. Neděli pak na Ládví v&nbsp;rámci PLAY HARD! TOUR ovládnou otevřené mixy. Všechny turnaje budou rozděleny na dvě výkonnostní kategorie podle umístění ve skupině tak, aby si zahrál každý pár zápasy na odpovídající úrovni. Ceny budou připraveny pro vítěze obou kategorií.</span></p><p><strong><span>Třetí víkend</span></strong><span>&nbsp;nás čekají&nbsp;<strong>sobotní turnaje</strong>&nbsp;<strong>ABL HOLMES PLACE mixy</strong>, které jsou pod taktovkou Ondry Michálka. U&nbsp;turnaje ABL se vracíme k&nbsp;názvu, proto turnaj bude určen&nbsp;<strong>pouze</strong>&nbsp;amatérským neregistrovaným dvojicím. Neděli uvolňujeme pro individuální hru a pronájmy.<br></span></p><p><span><img src=\"/files\\gallery\\3\\docs-482ac06ee8d434298f017c719d82d2b9.jpg\" width=\"570\"><br></span></p><p><strong><span>Čtvrtý víkend</span></strong><span>&nbsp;v&nbsp;měsíci věnujeme&nbsp;<strong>sérii otevřených výkonnostních turnajů</strong><strong>OAKLEY BEACH TOUR</strong>. Sobotní turnaje v&nbsp;Beachklubu Ládví odehrají páry mužů a žen, v neděli pak páry smíšené. Velkou výhodou je, že turnaj má omezený počet přihlášených párů v&nbsp;každé kategorii (v sobotu 12 párů mužů a 12 párů žen, v&nbsp;neděli 24 mixových párů).&nbsp;<em><strong>Všechny dvojice si tak zahrají minimálně pět kvalitních zápasů bez čekání!</strong></em>&nbsp;Všechny tři turnaje čtvrtého víkendu budou rozděleny na dvě výkonnostní kategorie podle umístění ve skupině tak, aby si zahrál každý pár na odpovídající úrovni. Ceny budou připraveny pro vítěze obou kategorií. Čtvrtý víkend v&nbsp;měsíci se odehraje pod vedením Ondry Michálka.</span></p><p><span><img src=\"/files\\gallery\\3\\docs-ae16300279ad4329d70b3659c1fd1a43.jpg\" width=\"570\"><br></span></p><p><strong style=\"font-family: BenchNine, sans-serif; font-size: 20px; line-height: 26px;\">O CO SE U NÁS V ZIMĚ HRAJE?</strong><br></p><p><span>V&nbsp;každé turnaji na Vás čekají zajímavé věcné ceny od našich partnerů<span>&nbsp;</span>.</span></p><p><span>Navíc však body za účast v&nbsp;turnajích Beachklubu Ládví sčítáme do celkového pořadí, kdy hrajeme o titul&nbsp;<strong>„KING/QUEEN&nbsp;Beachklubu Ládví“</strong>. Vítězem tohoto titulu bude buď muž, nebo žena na základě počtu získaných bodů ve všech turnajích pořádaných Beachklubem Ládví.</span></p><p><strong><span>Hlavní cenou</span></strong><span>&nbsp;pro jediného vítěze a zároveň jeho odměnou za perfektní výkony je&nbsp;<strong><span>dovolená v&nbsp;Tyrolsku</span></strong>&nbsp;od našeho partnera společnosti&nbsp;<strong>Tyrolsko</strong>(více info na&nbsp;</span><a href=\"http://www.tyrolsko.cz/\"><span>www.tyrolsko.cz</span></a>)<span>. Tyrolsko věnuje vítězi poukaz na&nbsp;<strong>2 noci s&nbsp;polopenzí pro 2 osoby v&nbsp;3* hotelu v&nbsp;Tyrolsku</strong>, s&nbsp;možností uplatnění poukazu do 31. 10. 2015. A o to už se vyplatí hrát!!!</span></p><p><br></p><p class=\"MsoNormal\"></p>',	'<p><br></p>',	2,	0,	0,	'turnaje-v-praze'),
(7,	'Staňte se členem Beachklubu Ládví a získejte zajímavé výhody:',	'<p>Hrajete plážový volejbal nebo se chystáte hrát plážový volejbal a rádi byste reprezentovali sportovní klub? Nebo ani nechcete nikoho reprezentovat, ale hledáte výhodnější možnosti jak si pronajmout kurty? Tak právě pro Vás jsme připravili dvě různá členství v našem klubu - standardni a rozšířené (občas používáme zkratku VIP). Čtěte dále, abyste poznali výhody a rozdíli.</p><h3>standardní členství v Beachklubu Ládví</h3><ul><li>zvýhodněná cena na předplatné</li><li>slevy na turnaje</li><li>slevy u partnerů</li><li>cena 0 Kč</li></ul><h3><strong><a href=\"https://docs.google.com/forms/d/1NwuRMvKz5qcaHZLXNfUQbqp5G3zsApAP4yRPhABMcfA/viewform?usp=send_form\" title=\"Přihláška\">Přihláška</a></strong></h3><h3>rožšířené členství v Beachklubu Ládví</h3><ul><li>kurty \"zdarma\" v pondělí až čtvrtek od 08.00 do 15.00</li><li>kurty \"zdarma\" v sobotu a neděli po turnajích</li><li>kurt zdarma má pouze VIP člen, nečleni platí zbývající poměrnou částku</li><li>slevy na turnaje</li><li>slevy u partnerů</li></ul><p><strong>cena:</strong>&nbsp;letní členství 1200 Kč</p><ul></ul><p>Pro mimořádně nadané hráče a hráčky, kteří by rádi reprezentovali náš klub vytvoříme rádi nabídku šitou na míru. Stačí napsat vaše výsledky a plány na email:&nbsp;<a href=\"mailto:marketing@beachklubladvi.cz\">marketing@beachklubladvi.cz</a></p>',	'<p><br></p>',	2,	0,	0,	'clenstvi'),
(8,	'Tréninky',	'<h3>Kondiční tréninky:</h3><p>Nyní máte možnost v Beachklubu zlepšovat svou kondici a zlepšovat si postavu díky našim kondičním tréninkům. Základní náplní těchto tréninků je zdravotně preventivní příprava, výkonnostní příprava a celkový rozvoj pohybových dovedností. Využíváme cvičení na plážovém písku, díky kterému se zapojuje více svalových partií a tím dochází ke komplexnějšímu zpevnění celého těla. Trénink je vhodný pro všechny věkové i výkonnostní kategorie.</p><p>Přijďte si s námi zacvičit pro svou radost z pohybu. Více info&nbsp;<a href=\"/kondicni-treninky\">zde</a>&nbsp;a přihlásit se můžete<a href=\"/treninky\">&nbsp;zde.</a></p><h3>Tréninkové beachvolejbalové turnaje:</h3><p>Chcete natrénovat na beachvolejbalový turnaj bez zbytečného stresu? Máte problém sehnat soupeře na stejné úrovni? Chcete si zahrát beachvolejbal s novými protihráči, ale nechcete trávit celý den na turnaji? Pokud jste si zodpověděli alespoň jednou ano, tak právě pro vás jsme připravili tréninkové beachvolejbalové turnaje.</p><p>Více&nbsp;<a href=\"/treninkove-turnaje-praha\">zde.</a></p><h3>Individuální tréninky plážového volejbalu:</h3><p>Kromě standardně vypisovaných kempů se můžete domluvit s našimi trenéry na individuálním beachvolejbalovém tréninku u nás v Praze na Ládví nebo i mimo Prahu. Kontakt na naše trenéry najdete&nbsp;<a href=\"/o-nas\">zde.</a></p><p>Na aktuální kempy tréninky dospělých se můžete přihlásit online&nbsp;<a href=\"/treninky\">zde.</a></p><p><span>V případě zájmu pište prosím našemu&nbsp;</span><a href=\"/o-nas\">šéftrenérovi</a><span>&nbsp;na e-mail</span><a href=\"mailto:seftrener@beachklubladvi.cz\">seftrener@beachklubladvi.cz</a><span>&nbsp;</span><br></p>',	'<p><br></p>',	2,	0,	0,	'treninky'),
(9,	'Beachvolejbalové kempy v Itálii již od 3.990,- Kč',	'<p><span>Chcete si užít týden u moře s přáteli a plážovým volejbalem? Špičkoví trenéři, příjemná atmosféra a kvalitní ubytování za vynikající ceny - to jsou beachvolejbalové kempy v italském Bibione, které tradičně připravil Beachklub Ládví ve spolupráci s cestovní kanceláří Nev-Dama ze skupiny Fischer. Těšit se můžete na široké písečné pláže, termální lázně, beach volejbalové tréninky 2x denně, cyklo a in-line vyjížďky každý den a kvalitní apartmány.</span><span>&nbsp;</span><span>No a chybět samozřejmě nemůže závěrečný turnaj, večerní víno, pizza a zpěv. Více o Bibione najdete</span><span>&nbsp;</span><a href=\"/kempy-v-bibione\">zde.</a></p><p></p><h3>Termíny beach volejbalových kempů v Itálii</h3><p><span>ZÁŘÍ 2015:</span><span>&nbsp;</span><span>19. - 26. 9. 2015&nbsp;(od soboty do soboty)</span></p><h3><span>Trenéři</span></h3><p>Těšit se můžete na kvalitní trenéry z řád bývalých špičkových volejbalistů z domácí extraligy, zahraničních soutěží a beachvolejbalového okruhu.&nbsp;<span>Těšit se můžete předběžně na&nbsp;</span><span>top trenéry Kamila Tučka a Míru Svobodu. V případě, že se naplní kapacita nad 32 účastníků, doplníme trenérský tým o třetího trenéra.</span></p><h1><p></p></h1><h3>Program beachkempu a rozpis beach volejbalových tréninků</h3><p>Účastníci kempu budou rozděleni podle výkonnosti do 8 skupin max. po 8 lidech. Kapacita beach kempu je tedy 64 hráčů. Těšit se můžete na 2 beachvolejbalové tréninky denně po 1,5 hodině na 4 kurtech a k tomu ranní výklusy po pláži, odpolední vyjížďky na kole nebo na in-linech a další sportovní aktivity.</p><h1><ul><li><span>sobota - příjezd, ubytování, uvítací večírek</span></li><li>neděle - čtvrtek - tréninky, hra, sportovní program, středa - volný program na regeneraci</li><li>pátek - závěrečný turnaj</li><li>sobota - odjezd</li></ul></h1><p><span><span>Beachkempy jsou vhodné i pro rodiny s dětmi. V případě poptávky budou organizovány i dětské tréninky.</span></span></p><h3>Ubytování: Villagio Michelangelo, Tivoli, San Siro</h3><p><span>Ubytování je zařízeno ve spolupráci s cestovní kanceláří Nev-Dama (ze skupiny Fischer) v komplexu dvoupodlažních vilek s několika bazény (Michelangelo) a sousedním menším vilovém rezortu s jedním bazénem (Tivoli), který je vzdálený asi 550 metrů od pláže. Podle zájmu si můžete vybrat z různých velikostí apartmánů. K dispozici jsou apartmány typu mono, bilo, trilo. Kompletní popis najdete</span><span>&nbsp;</span><a href=\"/kempy-v-bibione\">zde.</a></p><h1><p><img src=\"/files\\gallery\\3\\docs-56957af8ac1c8e62af3f101816be2ec6.jpg\" alt=\"beach volejbalové kempy v Itálii\" width=\"570\"></p></h1><h3>Ceník beach volejbalových kempů v Itálii</h3><h3><span>ZÁŘÍ 2015 cena od 3.990 Kč</span></h3><h3><span>Trilo (min. obsazenost 4 osoby)</span><span>&nbsp;</span><strong><span>4.490,- Kč</span></strong><span>,</span><span>&nbsp;</span><span>pro členy klubu nebo účastníky kempů v&nbsp;Bibione v&nbsp;roce 2014 a 2015&nbsp;<strong>3.9</strong><strong>90,-</strong>&nbsp;<strong>Kč</strong></span></h3><h3><span><strong></strong></span><span>Bilo (min. obsazenost 3 osoby)</span><span>&nbsp;</span><strong><span>4.690,- Kč,&nbsp;</span></strong><span>pro členy klubu nebo účastníky kempů v&nbsp;Bibione v&nbsp;roce 2014 a 2015&nbsp;<strong>4.190,-</strong>&nbsp;<strong>Kč</strong></span></h3><h3><span><strong></strong></span><span>Mono (min. obsazenost 2 osoby)</span><span>&nbsp;</span><strong><span>5.090,-</span></strong><span>&nbsp;</span><strong><span>Kč</span></strong><span>,</span><span>&nbsp;</span><span>pro členy klubu nebo účastníky kempů v&nbsp;Bibione v&nbsp;roce 2014 a 2015&nbsp;<strong>4.590,-</strong>&nbsp;<strong>Kč</strong></span></h3><h1><div><div><p>* Cena zahrnuje ubytování, veškeré spotřeby, delegátské služby, kurty, tréninky a závěrečný turnaj.</p></div></div></h1><p>Možné slevy a příplatky (nepovinné)</p><h1><div><ul><li>sleva pro netrénující účastníky 1.300 Kč</li><li>příplatek za nocleh až do neděle 300 Kč</li><li>autobusová doprava: 1.990 Kč dospělí, 1.790 Kč děti do 12 let</li><li>ložní prádlo 200 Kč</li><li>závěrečný úklid dle typu bytu: mono 700 Kč, bilo 900 Kč, trilo 1.100 Kč</li><li>cestovní pojištění 290,-</li></ul></div></h1><h3>Přihlášky</h3><p><span>Přihlášky</span><span>&nbsp;</span><span>posílejte Lucii Trnkové na email</span><span>&nbsp;</span><a href=\"mailto: &lt;script language=\'JavaScript\' type=\'text/javascript\'&gt; &lt;!-- var prefix = \'mailto:\'; var suffix = \'\'; var attribs = \'\'; var path = \'hr\' + \'ef\' + \'=\'; var addy65409 = \'info\' + \'@\'; addy65409 = addy65409 + \'beachklubladvi\' + \'.\' + \'cz\'; document.write( \'&lt;a \' + path + \'\\\'\' + prefix + addy65409 + suffix + \'\\\'\' + attribs + \'&gt;\' ); document.write( addy65409 ); document.write( \'&lt;\\/a&gt;\' ); //--&gt; &lt;/script&gt; &lt;script language=\'JavaScript\' type=\'text/javascript\'&gt; &lt;!-- document.write( \'&lt;span style=\\\'display: none;\\\'&gt;\' ); //--&gt; &lt;/script&gt;Tato emailov%C3%A1 adresa je chr%C3%A1n%C4%9Bna p%C5%99ed spamboty, abyste ji vid%C4%9Bli, povolte JavaScript &lt;script language=\'JavaScript\' type=\'text/javascript\'&gt; &lt;!-- document.write( \'&lt;/\' ); document.write( \'span&gt;\' ); //--&gt; &lt;/script&gt;\"></a><a href=\"mailto:info@beachklubladvi.cz\">info@beachklubladvi.cz</a>&nbsp;<span>. Uveďte prosím Vaše jméno, příjmení, datum narození, e-mail, telefon, v jaké typologii apartmánu chcete být ubytování a s kým. A jaké chcete využít slevy či příplatky.&nbsp;</span><strong>POZOR!&nbsp;</strong><span>Nově do přihlášky ohodnoťte i úroveň svých volejbalových dovedností známkou jako ve škole (1 - nejlepší, pokročilý; 2 - středně pokročilý; 3 - přechod mezi pokročilým a mírně pokročilým; 4 - mírně pokročilý; 5 - začátečník). Podle toho určíme i předběžné zařazení do tréninkových skupin. Následně vám zašleme informace k platbě.</span></p><p><span>Spolu s přihláškou&nbsp;</span>vyplňte svou účast i prostřednictvím našeho rezervačního systému<span>, kde jsou jednotlivé turnusy vypsané -&nbsp;</span><a href=\"/kempy-v-bibione\" title=\"Rezervační systém\">zde</a><span>. Můžete sledovat, kdo je na jednotlivé turnusy přihlášen.</span></p><p>Uzávěrka přihlášek je&nbsp;<strong>30. 8</strong><strong>. 2015.</strong></p><p><strong></strong>Záloha ve výši 500,- Kč je splatná do 10 dní od odeslání údajů k platbě a doplatek do 30. 8. 2015.</p><p>Storno poplatky: více jak 14 dní před kempem 50% z ceny kempu, méně jak 14 dní 100%. V případě, že za sebe najdete náhradníka, vracíme celou částku. Doporučujeme uzavřít storno pojištění pro případ nemoci.</p><p><strong>Veškeré bližší informace a dotazy poskytne Lucka Trnková na mailu<a href=\"mailto: &lt;script language=\'JavaScript\' type=\'text/javascript\'&gt; &lt;!-- var prefix = \'mailto:\'; var suffix = \'\'; var attribs = \'\'; var path = \'hr\' + \'ef\' + \'=\'; var addy8849 = \'info\' + \'@\'; addy8849 = addy8849 + \'beachklubladvi\' + \'.\' + \'cz\'; document.write( \'&lt;a \' + path + \'\\\'\' + prefix + addy8849 + suffix + \'\\\'\' + attribs + \'&gt;\' ); document.write( addy8849 ); document.write( \'&lt;\\/a&gt;\' ); //--&gt; &lt;/script&gt; &lt;script language=\'JavaScript\' type=\'text/javascript\'&gt; &lt;!-- document.write( \'&lt;span style=\\\'display: none;\\\'&gt;\' ); //--&gt; &lt;/script&gt;Tato emailov%C3%A1 adresa je chr%C3%A1n%C4%9Bna p%C5%99ed spamboty, abyste ji vid%C4%9Bli, povolte JavaScript &lt;script language=\'JavaScript\' type=\'text/javascript\'&gt; &lt;!-- document.write( \'&lt;/\' ); document.write( \'span&gt;\' ); //--&gt; &lt;/script&gt;\"></a><a href=\"mailto:info@beachklubladvi.cz\">info@beachklubladvi.cz</a>&nbsp;nebo na telefonu 728 189 731.</strong></p>',	'<p><br></p>',	2,	0,	0,	'kempy-v-italii'),
(10,	'Beachkempy v Čechách',	'<h1 style=\"padding-right: 10px; padding-bottom: 5px; padding-left: 10px; margin-top: 0px; margin-bottom: 0px; border: none;\">Beachvolejbalové kempy na Ládví pod vedením Jardy Malčíka</h1><p>Nově nabízíme prodloužené víkendy All Inclusive přímo v areálu Beachklubu Ládví pro pražské i přespolní.<br>Intenzivní tréninky 2x denně zaměřené na zlepšení herních činností jednotlivce. Tréninkové skupiny: a) začátečníci, b) mírně pokročilí, c) pokročilí (rozdělení do skupin podle přihlášených účastníků). Hlavní trenér těchto kempů je Jaroslav Malčík (šéftrenér mládeže).</p><p>&nbsp;</p><p><img src=\"/files\\gallery\\3\\docs-2368d823b062de107596afb71282ee40.jpg\" width=\"570\"></p><h3>Termíny:</h3><p>10. - 12. 7. a 14. - 16. 8.</p><h3>Ubytování:</h3><p>500 m od Beachklubu v Hostelu Ládví</p><h3>Kapacita kempu:</h3><p>16 osob. Kemp se uskuteční při minimálním počtu 12 osob.<br>2 tréninkové skupiny 6 - 8 osob</p><p><img src=\"/files\\gallery\\3\\docs-e645b33b64042eb7299cc8506403dd85.jpg\" width=\"570\"></p><h3>Plná penze + pitný režim:</h3><p>Snídaně: formou bufetu, čaj, káva<br>Oběd: výběr ze 2 jídel + polévka dle denní nabídky<br>Večeře: Gril<br>Vše na baru Beachklubu Ládví.</p><h3>Program beach kempu:</h3><p>Pátek<br>09:00 - příjezd a ubytování<br>10:00 - 13:00 rozřazovací hra<br>15:00 - 18:00 tréninky<br>Sobota<br>09:00 - 12:00 tréninky<br>15:00 - 18:00 tréninky<br>Neděle<br>09:00 – 12:00 tréninky<br>14:00 – 17:00 turnaj</p><p>V době mimo tréninky individuální program:</p><p>•&nbsp;dobrá dostupnost do centra Prahy, stanice metra Ládví (centrum 10 min.)<br>•&nbsp;program bez večerky - noční život na dosah ruky<br>•&nbsp;velký areál s možností využití kurtů mimo tréninky<br>•&nbsp;možnost využití doprovodných aktivit pétanque, kroket, velkoplošná TV, bungee running – vše v areálu Beachklubu Ládví</p><h3>Cena:</h3><p><strong>2550,- Kč / osoba</strong><br>Cena 2.550,- Kč / osoba za kemp je trenér, pronájem kurtů, míče a tréninkové pomůcky, video výstup, strava od pátku do neděle (snídaně, oběd, večeře), pitný režim, ubytování (2 noci v Hostelu Ládví, na pokoji 2 – 5 osob).<br>1950,-Kč / osoba (bez ubytování)</p><p>Částku uhraďte převodem na číslo účtu 107-5561340227/0100 nejpozději měsíc před začátkem daného kempu. Nezapomeňte uvést své jméno.</p><h3>Přihlášky:</h3><p>Přihlásit se můžete e-mailem na&nbsp;<a href=\"mailto:info@beachklubladvi.cz\">info@beachklubladvi.cz</a>&nbsp;nebo online na<a href=\"http://www.beach-ladvi.rezervuju.cz/training\">http://www.beach-ladvi.rezervuju.cz/training</a>.</p><h1 style=\"padding-right: 10px; padding-bottom: 5px; padding-left: 10px; margin-top: 0px; margin-bottom: 0px; border: none;\">Beachvolejbalové kempy u Máchova jezera</h1><table class=\"contentpaneopen\"><tbody><tr><td valign=\"top\"><p>Díky velkému zájmu a pozitivním ohlasům Beachklub Ládví rozšiřuje nabídku intenzivních letních beachvolejbalových kempů o kemp na „Mácháči“.<br>Abyste se měli na co těšit, podrobné info o místě konání naleznete&nbsp;<a href=\"http://www.kudyznudy.cz/Aktivity-a-akce/Aktivity/Romantika,-slunce,-vodni-atrakce---to-je-Machac-pr.aspx\">zde</a>.</p><p><img src=\"/files\\gallery\\3\\docs-11c1b918ab32bcdaa1bced6c6625cab7.jpg\" width=\"570\"></p><h3><img src=\"/files\\gallery\\3\\docs-f3d5a6adeb5a52f4fb2266cfc055e097.jpg\" width=\"570\"></h3><h3>Termín:</h3><p>12.6. – 14.6.2015</p><h3>Ubytování:</h3><p>Ubytování je zajištěno v penzionu SPORTline - Penzion &amp; FitCentrum Doksy.<br>Adresa: Lesní 458, Doksy, 472 01</p><p><img src=\"/files\\gallery\\3\\docs-a4973725dc9622d0780219b013d2cdd7.jpg\" width=\"570\"></p><p><img src=\"/files\\gallery\\3\\docs-acdf91d3dc911e8da173eefcbc24d0e2.jpg\" width=\"570\"></p><p>V ceně ubytování je zároveň zahrnut vstup do fitness zdarma, free WiFi a parkovací místo. Dále&nbsp;<span>mají&nbsp;</span><span>ubytování nárok na 25% slevu na sál na cvičení a spinning.</span></p><h3>Kapacita kempu:</h3><p>32 osob. Kemp se uskuteční při minimálním počtu 12 osob.</p><h3>Beachvolejbalové tréninky:</h3><p>Beachvolejbalové tréninky budou probíhat na kurtech přímo u Máchova jezera pod vedením šéftrenéra Beachklubu Ládví Ondry Michálka a špičkového hráče beachvolejbalu a trenéra s dlouholetými zkušenostmi Míry Svobody.<br>Rozdělení do 4 tréninkových skupin po 6 - 8 lidech (podle počtu přihlášených).</p><h3>Program beach kempu:</h3><p>Pátek<br>09:00&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; příjezd a ubytování<br>10:00 - 13:00&nbsp; rozřazovací hra<br>15:00 - 18:00&nbsp; tréninky</p><p>Sobota<br>09:00 - 12:00&nbsp; tréninky<br>15:00 - 18:00&nbsp; tréninky</p><p>Neděle<br>09:00 – 12:00&nbsp; tréninky<br>14:00 – 17:00&nbsp; turnaj</p><p><img src=\"/files\\gallery\\3\\docs-c71e592e58d6be786f8e2fc3485d4fa8.jpg\" width=\"570\"></p><p>V době mimo tréninky individuální program:</p><p>- krásná pláž Máchova jezera<br>- kvalitní a hustá síť cyklostezek&nbsp;<br>- středověké hrady (Bezděz, Houska, Kokořín, Lemberk)<br>- mnoho restaurací a kaváren v Doksech<br>- okružní plavby po jezeře</p><p>A to vše v bezprostřední blízkosti kurtů!</p><h3>Cena:</h3><p><strong>2.500,- Kč / osoba</strong><br>Cena zahrnuje ubytování se snídaní, tréninky s trenérem, kurty ke hře, tréninkové pomůcky, vstup na pláž jezera po celou dobu kempu.</p><p><strong>1.900,- Kč / osoba</strong><br>Cena je bez ubytování, zahrnuje tréninky s trenérem, kurty ke hře, tréninkové pomůcky, vstup na pláž jezera po celou dobu kempu.</p><p>Částku uhraďte převodem na číslo účtu 107-5561340227/0100 poté, co obdržíte variabilní symbol. Nejpozději uhraďte částku měsíc před začátkem daného kempu. Nezapomeňte uvést své jméno.</p><h3>Přihlášky:</h3><p>Přihlásit se můžete e-mailem na&nbsp;<a href=\"mailto:info@beachklubladvi.cz\">info@beachklubladvi.cz</a>&nbsp;nebo online na<a href=\"http://www.beach-ladvi.rezervuju.cz/training\">http://www.beach-ladvi.rezervuju.cz/training</a>.</p><p><span style=\"font-family: BenchNine, sans-serif; font-size: 50px; font-weight: 700; line-height: 53px; text-transform: uppercase; background-color: transparent;\">Beachvolejbalové kempy v Poděbradech</span></p><p>Díky velkému zájmu a pozitivním ohlasům Beachklub Ládví rozšiřuje nabídku intenzivních letních beachvolejbalových kempů v Poděbradech.<br>Abyste se měli na co těšit, podrobné info o místě konání naleznete&nbsp;<a href=\"http://prekvapeni.kafe.cz/magazin/cestovani/prekvapeni-na-cestach/rozkvetle-podebrady_652.html\">zde</a>.</p><h3>Termíny:</h3><p>29.5. – 31.5.2015 (obsazeno)<br>26.6. – 28.6.2015 (obsazeno)<br>04.7.&nbsp;–&nbsp;06.7.2015 (pouze pro začátečníky)<br>14.8. – 16.8.2015<br>28.8. – 30.8.2015</p><h3>Ubytování:</h3><p>Ubytování v nově zařízeném hotelu Junior v centru Poděbrad. Více info&nbsp;<a href=\"/kempy-v-cechach\">zde</a>.</p><h3>Kapacita kempu:</h3><p>16 osob. Kemp se uskuteční při minimálním počtu 10 osob.</p><h3>Beachvolejbalové tréninky:</h3><p>Beachvolejbalové tréninky budou probíhat na kurtech koupaliště v Poděbradech pod vedením šéftrenéra Beachklubu Ládví Ondry Michálka, případně Míry Justiána.<br>Rozdělení do 2 tréninkových skupin po 8 lidech (podle počtu přihlášených).</p><h3>Program beach kempu:</h3><p><strong>Pátek</strong><br>09:00 - příjezd a ubytování<br>10:00 - 13:00 rozřazovací hra<br>15:00 - 18:00 tréninky</p><p><strong>Sobota</strong><br>09:00 - 12:00 tréninky<br>15:00 - 18:00 tréninky</p><p><strong>Neděle</strong><br>09:00 – 12:00 tréninky<br>14:00 – 17:00 turnaj</p><p>V době mimo tréninky individuální program:</p><p>-	vyhlášené písečné jezero<br>-	kvalitní inline stezky&nbsp;<br>-	nespočet kaváren a restaurací<br>-	lázeňská kolonáda</p><p>A to vše v bezprostřední blízkosti kurtů!</p><h3>Cena:</h3><p><strong>2.210,- Kč / osoba</strong></p><p>Pro členy klubu nebo účastníky loňského kempu v Poděbradech 1.910,- Kč / osoba.<br>Cena zahrnuje ubytování se snídaní, tréninky s trenérem, kurty ke hře, vstup na koupaliště a v neposlední řadě průvodce krásami Poděbrad.</p><p>Částku uhraďte převodem na číslo účtu 107-5561340227/0100 nejpozději měsíc před začátkem daného kempu. Nezapomeňte uvést své jméno.</p><h3>Přihlášky:</h3><p>Přihlásit se můžete e-mailem na&nbsp;<strong><a href=\"mailto:info@beachklubladvi.cz\">info@beachklubladvi.cz</a>&nbsp;</strong>nebo online na<a href=\"http://www.beach-ladvi.rezervuju.cz/training\"><strong>http://www.beach-ladvi.rezervuju.cz/training</strong></a>.<br></p><h1 style=\"padding-right: 10px; padding-bottom: 5px; padding-left: 10px; margin-top: 0px; margin-bottom: 0px; border: none;\">Prázdninové beachvolejbalové příměstské tábory pro děti</h1><p><img src=\"/files\\gallery\\3\\docs-3899ebb87c1aea0e0348d380aae5f2ad.jpg\" width=\"570\"></p><h3>Letní příměstský beachvolejbalový tábor</h3><p>Beachklub Ládví pořádá prázdninové příměstské tábory v plážovém volejbalu pro děti. Beachvolejbalový intenzivní kemp s výukou angličtiny se zkušenými lektory soukromé jazykové školy<strong>&nbsp;Fresh School</strong>.</p><h3>Termíny prázdninových beachvolejbalových kempů</h3><p>Na letní prázdniny 2015 vypisujeme díky zájmu z předchozích let 4 termíny beachvolejbalových kempů. Dva na červenec a dva na srpen. Turnus vždy začíná v pondělí ráno a končí v pátek odpoledne.</p><p>I. turnus: od 6. do 10. července<br>II. turnus: od 13. do 17. července<br>!!! NOVÝ TURNUS!!! : od 10. do 14. srpna<br>III. turnus: od 17. do 21. srpna (obsazeno)<br>IV. turnus: od 24. do 28. srpna (obsazeno)</p><h3>Program prázdninových beachvolejbalových kempů</h3><p>Nástup na kemp v pondělí v 8:30 hod. v areálu Beachklubu Ládví.</p><p>08:30 - 9:00 příchod - svačina<br>09:30 - dopolední tréninková jednotka<br>11:00 - relaxace (k dispozici doplňkové sporty jako badminton, pétanque, trampolína)<br>11:30 - oběd<br>12:00 - 13:00 - výuka AJ - výuku garantují lektoři z jazykové školy Fresh School<br>14:00 - odpolední tréninková jednotka<br>16:00 - 16:30 svačina a vyzvednutí rodiči</p><p><br>V pátek odpoledne dětský turnaj kempařů ukončený předáním cen a vyhlášením nejlepších účastníků turnaje.</p><h3>Co připravit dětem na kemp:</h3><p>Prosíme kromě sportovního oblečení (2x tričko, trenky či tepláky) zabalte dětem do tašky i lahev na pití, sluneční brýle, pokrývku hlavy a krém na opalování. V případě špatného počasí i sportovní boty. Pro případ deště máme v areálu Beachklubu Ládví i tělocvičnu.</p><h3>Cena beach volejbalového kempu:</h3><p><strong>2.490,- Kč</strong></p><p>Pro členy klubu, děti s trvalým bydlištěm v Praze 8 nebo účastníky loňského příměstského tábora 2.100 Kč.</p><p>Cena kromě kurtů, míčů a tréninkových jednotek zahrnuje i snídani, oběd, svačinu a pitný režim v průběhu celého dne, kempové tričko značky Under Armour a výuku angličtiny.</p><p>Přihlásit se můžete mailem na&nbsp;<strong><a href=\"mailto:info@beachklubladvi.cz\">info@beachklubladvi.cz</a>&nbsp;</strong>a online v<a href=\"http://beach-ladvi.rezervuju.cz/training\">rezervačním systému</a>.</p><p><img src=\"/files\\gallery\\3\\docs-18291511ca6ec20c17e4828151ecd0a2.jpg\" width=\"570\"></p><p><img src=\"/files\\gallery\\3\\docs-b321ae066eaaa3f32fb96cfad4928d10.jpg\" width=\"570\"></p><p><img src=\"/files\\gallery\\3\\docs-b24774fffa09c04742962aea2d8b91fb.jpg\" width=\"570\"></p></td></tr></tbody></table><div><br></div>',	'<p><br></p>',	2,	0,	0,	'kempy-v-cechach'),
(12,	'Celoroční kroužky bechvolejbalu a všeobecného pohybového rozvoje pro děti',	'<h3>Beachklub Ládví se zaměřuje na výchovu mládeže od 3 do 18 let</h3><p>Díky pískovému pokladu je náš areál ideální průpravou na všeobecný pohybový rozvoj. Na nezpevněném povrchu se svěřenci učí lepší stabilitě a každý pohyb je náročnější, přesto šetrnější na pohybový aparát.</p><h3>Kroužky pro radost</h3><p>Dětská atletická přípravka je určena pro děti od 3 do 5 let, na ni volně navazují beachvolejbalové kroužky pro děti od 6 do 9 let, které jsou zaměřené na všeobecný pohybový rozvoj. Volejbalové základy trénují děti v kroužcích od 10 do 13 let. Na ně dále navazuje nejstarší věková kategorie pro děti od 14 do 18 let.&nbsp;<span>Všechny kategorie jsou smíšené.</span></p><p><span>Tyto kroužky jsou vedené jako rekreační, bez důrazu na výkonnost a výsledky. Prioritou těchto tréninků je naučit děti základy tohoto sportu a správným sportovním návykům, které si přenesou i do osobního života.</span></p><h3>Výkonnostní tréninky</h3><p><span>Díky široké mládežnické základně, kterou areál v současné době disponuje, se chceme zaměřit také na výkonnostní volejbal.&nbsp;</span><span>Je to další krok Beachklubu Ládví ve výchově a motivaci našich svěřenců.</span></p><p>Tyto výkonností tréninky budou zaměřené na děti s fyzickými a mentálními předpoklady pro tento sport. Cílem této tréninkové skupiny bude vychovávat budoucí reprezentanty našeho areálu a naší beach akademie. Tréninky budou vedeny individuálně s důrazem na rozvoj fyzických i technických schopností svěřenců.</p><p>Tréninky 4x týdně:</p><p>- 2x výkonnostní trénink, trenér Jarda Malčík (skupina 3 - 8 dětí), trénink na písku</p><p>- 1x kondiční trénink &nbsp;( skupina 3-8 dětí), trénink na písku</p><p>- 1x trénink šestkového volejbalu v hale</p><p><span>Výkonnostní trénink pro rozvoj techniky a fyzických dispozic svěřence. Šestkový volejbal pro rozvoj dynamiky, výbušnosti a zlepšení techniky.</span></p><h3>Rozpis beach volejbalových tréninků</h3><h3>Přípravka 3 - 5 let</h3><p>Pondělí, středa, čtvrtek 15:15 - 16:00 + středa 16:00 - 16:45</p><p>(vstupné na trénink pro nejmenší je Ládvík - cena Ládvíka 60,- Kč - opravňuje dítě ke vstupu na 1 trénink)</p><h3>Kroužky pro děti 6 - 9 let</h3><p>Pondělí až čtvrtek 16:00 - 17:00</p><p>(možnost výběru s ohledem a školu a ostatní aktivity dětí: pondělí - středa nebo úterý - čtvrtek)</p><h3>Kroužky pro děti 10 - 13 let</h3><p>Pondělí až čtvrtek 17:00 - 18:00</p><p>(možnost výběru s ohledem na školu a ostatní aktivity dětí: pondělí - středa nebo úterý - čtvrtek)</p><h3>Kroužky pro děti 14 - 18 let</h3><p>Úterý a čtvrtek 16:00 - 17:00</p><p>+ Pondělí&nbsp; a pátek 16:00 - 17:00</p><p>Trenéři: trenéry doplníme během prvního týdne kroužků</p><p>Každá tréninková skupina bude mít svého stálého trenéra, který skupinu povede po celou dobu školního roku.</p><h3>Cenová nabídka</h3><p>Vše se předplácí formou dobití čipové karty</p><p>1) 10 kreditů, které se dají libovolně vybrat bez časového omezení.&nbsp;<strong>Cena 1000,- Kč&nbsp;</strong>(1 trénink = 100,-).</p><p>2) Pololetní 5ti měsíční. Karta je na jméno a opravňuje majitele navštěvovat 2x týdně tréninky mládeže, využívat bezplatně kurty v případě, že jsou mimo špičku volné.<strong>&nbsp;Cena 3.150,- Kč&nbsp;</strong>(1 trénink = 75,- Kč).</p><p>3) Pololetní výkonnostní. Karta je na jméno a opravňuje majitele být členem tréninkové výkonnostní skupiny, trénink v uzavřené skupince (max. 8 dětí) 4x týdně, pravidelná nominace a účast na turnajích Juniorligy pod vedením trenéra, dresy pro sezónu 2014 - 15, klubové výhody pro regeneraci. Bezplatné využití kurtů mimo špičku, prezentace úspěšných dvojic na webu beachklubu. Nominaci do výkonnostních skupin řeší hlavní trenér mládeže a rodiče oslovených dětí. Cena 3990,- Kč (cena je dotována Beachklubem Ládví, aby naši nadějní svěřenci měli možnost trénovat za špičkových podmínek za přijatelnou cenu).</p><p><strong><span>Pololetní permanentka platnost:</span></strong></p><p><strong>1 pololetí&nbsp;</strong>1. září 2014 - 31. ledna 2015 (možnost zakoupení 1. - 14. 9. na recepci Beachklubu Ládví)</p><p><strong>2 pololetí</strong>&nbsp;1. února 2014 - 29. června 2015&nbsp;&nbsp;<span></span><span>(možnost zakoupení 1. - 14. 9. na recepci Beachklubu Ládví)</span></p><h3>Klubová přihláška</h3><p><strong>Všichni naši kroužkaři s pololetní permanentkou mají možnost využívat bezplatně areál ve volných hodinách, bezplatně se účastnit dětských akcí pořádaných Beachklubem Ládví.</strong></p><p>Přihláška ke stažení ve formátu&nbsp;<a href=\"http://www.beachklubladvi.cz/images/krouzky.pdf\">.pdf</a>&nbsp;neboj&nbsp;<a href=\"http://www.beachklubladvi.cz/images/krouzky.jpg\">.jpg</a><strong>.<br></strong></p><p>Pokud chcete pravidelně chodit do našich kroužků, kontaktujte našeho šéftrenéra Ondru Michálka na e-mail&nbsp;<span><a href=\"mailto:seftrener@beachklubladvi.cz\">seftrener@beachklubladvi.cz</a>&nbsp;</span>nebo volejte na telefon: 605 218 056.</p><p><img src=\"/files\\gallery\\3\\docs-fa39256f480fa51d1f70cb3890aaec36.jpg\" width=\"570\"></p><p><img src=\"/files\\gallery\\3\\docs-c718eebf750cc240cc0e37c4b7afa756.jpg\" width=\"570\"></p><p><img src=\"/files\\gallery\\3\\docs-1a7cd302290d30cb2d4156476bfbb97c.jpg\" width=\"570\"></p>',	'<p><br></p>',	2,	0,	0,	'deti-a-mladez'),
(13,	'Beach volejbalové kempy pro maminky',	'<p>Připravili jsme kempy plážového volejbalu pro maminky s malými dětmi. Máme zajištěné hlídání a pěkně vybavený dětský koutek, takže si nemusíte dělat starosti s tím, kdo vám pohlídá ratolesti. Kempy jsou vhodné pro maminky, které se chtějí po porodu chtějí vrátit zpátky do formy nebo se jen chtějí odreagovat. Přijít můžete samozřejmě, i když žádné děti nemáte, ale vyhovuje vám čas a náplň tréninku.</p><h3>Treninková náplň beachvolejbalových kempů pro maminky:</h3><ul><li>protažení</li><li>zahřátí</li><li>nácvik základních herních činností</li><li>simulace herních situací</li><li>hra samotná</li><li>konečné protažení</li></ul><h3>Délka tréninkové jednotky:</h3><p>120 minut</p><h3>Ceník beachvolejbalového kempu pro maminky:</h3><p>1&nbsp;lekce - 250 Kč při zakoupení permanentky 5+1) splatná před zahájením tréninku, cena zahrnuje 90 min. tréninku, 30 min. hry a dvě hodiny hlídání dětí zkušenými au-pair; v případě zájmu je možné objednat čerstvé jídlo pro děti i miminka z restaurace Na Krásné Vyhlídce.</p><h3>Termíny beachvolejbalových kempů pro maminky:</h3><p>středa: 10:00 - 12:00</p><p>čtvrtek: 09:00 - 11:00</p><p>Tréninková jednotka max. pro 16 osob na 2 tréninkové skupiny podle výkonnosti. Pod vedením trenérů Ondry Michálka a Jardy Malčika</p><p>Online rezervace beachvolejbalových kempů pro maminky&nbsp;<a href=\"http://www.beach-ladvi.rezervuju.cz/training\">zde.</a></p>',	'<p><br></p>',	2,	0,	0,	'maminky'),
(14,	'Beachvolejbalové kempy',	'<p>Beachvolejbalové kempy organizujeme v Beachklubu Ládví každý den od 18.00 a jsou ideální pro dospělé začátečníky a mírně pokročilé. Páteční hrací tréninky jsou naopak určené spíše pokročilejším hráčům</p><h2 style=\"padding-right: 10px; padding-bottom: 5px; padding-left: 10px; margin: 0px; border: none;\">Tréninkové kempy plážového volejbalu</h2><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Náplň tréninkové jednotky:</h3><ul style=\"margin-right: 5px; margin-bottom: 0px; margin-left: 5px;\"><li style=\"border: none; float: none; clear: both;\">Protažení</li><li style=\"border: none; float: none; clear: both;\">Zahřátí</li><li style=\"border: none; float: none; clear: both;\">Nácvik základních herních činností</li><li style=\"border: none; float: none; clear: both;\">Simulace herních situací</li><li style=\"border: none; float: none; clear: both;\">Hra samotná</li><li style=\"border: none; float: none; clear: both;\">Konečné protažení</li></ul><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Délka tréninkové jednotky:</h3><p style=\"border: none; float: none; clear: both; padding: 0px 10px 0px 30px;\">90 min. a 60 min.</p><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Ceník beach volejbalových kempů:</h3><p style=\"border: none; float: none; clear: both; padding: 0px 10px;\">1&nbsp;lekce 90 min. - 200 Kč (při zakoupení permanentky 5+1) 60 min.&nbsp;&nbsp;150 Kč (při zakoupení permanentky 5+1)</p><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Termíny beach volejbalových kempů:</h3><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Online rezervace:</h3><p style=\"border: none; float: none; clear: both; padding: 0px 10px;\">Místo na kempu si můžete rezervovat online&nbsp;<a href=\"http://www.beach-ladvi.rezervuju.cz/training\">zde.</a></p><p style=\"border: none; float: none; clear: both; padding: 0px 10px;\">V případě, že byste chtěli znát více podrobností, pište prosím Ondrovi Michálkovi&nbsp;na e-mail:&nbsp;<a href=\"mailto:seftrener@beachklubladvi.cz\">seftrener@beachklubladvi.cz</a>&nbsp;nebo volejte na telefon: 605 218 056</p><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Sankce</h3><p style=\"border: none; float: none; clear: both; padding: 0px 10px;\">V případě neodhlášení 12 hodin před konáním tréninkového kempu zaplatíte při dalším kempu pokutu 100,- Kč!!!</p><h2 style=\"padding-right: 10px; padding-bottom: 5px; padding-left: 10px; margin: 0px; border: none;\">Páteční beachpárty</h2><p style=\"border: none; float: none; clear: both; padding: 0px 10px;\">Každý pátek organizujeme \"beachpárty\", které jsou věnované především hře a pravidelně se jich účastní až 24 hráčů. Úroveň je určena pro středně pokročilé a pokročilejší hráče.</p><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Treninková náplň:</h3><ul style=\"margin-right: 5px; margin-bottom: 0px; margin-left: 5px;\"><li style=\"border: none; float: none; clear: both;\">Protažení</li><li style=\"border: none; float: none; clear: both;\">Turnaj</li><li style=\"border: none; float: none; clear: both;\">Konečné protažení</li></ul><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Délka tréninkové jednotky:</h3><p style=\"border: none; float: none; clear: both; padding: 0px 10px 0px 30px;\">2,5 - 3 Hod</p><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Ceník:</h3><p style=\"border: none; float: none; clear: both; padding: 0px 10px;\">200 Kč ( Při zakoupení permanentky 5+1)</p><h2 style=\"padding-right: 10px; padding-bottom: 5px; padding-left: 10px; margin: 0px; border: none;\">Rezervace:</h2><p style=\"border: none; float: none; clear: both; padding: 0px 10px;\">Místo na kempu si můžete rezervovat online&nbsp;<a href=\"http://www.beach-ladvi.rezervuju.cz/training\">zde</a></p><p style=\"border: none; float: none; clear: both; padding: 0px 10px;\">V případě zájmu pište prosím našemu&nbsp;<a href=\"http://www.beachklubladvi.cz/treneri.html\">šéftrenérovi</a>&nbsp;na e-mail:<a href=\"mailto:seftrener@beachklubladvi.cz\">seftrener@beachklubladvi.cz</a>&nbsp;nebo volejte na telefon: 605 218 056</p><h3 style=\"padding-right: 10px; padding-bottom: 0px; padding-left: 10px; margin: 0px; border: none;\">Sakce</h3><p style=\"border: none; float: none; clear: both; padding: 0px 10px;\">V případě neodhlášení 12 hodin před konáním tréninkového kempu zaplatíte při dalším kempu pokutu 100,- Kč!!!</p>',	'<p><br></p>',	2,	0,	0,	'dospeli'),
(15,	'Kondiční tréninky nejen pro sportovce',	'<p>Beachklub Ládví nově otevírá nedělní kondiční tréninky. Nyní máte možnost si neobvyklým způsobem splnit svá novoroční předsevzetí o lepší kondici a krásné postavě. Vyměňte fitness studia, vyzkoušejte pohyb v jemné písku, díky kterému je trénink intenzivnější a přitom šetrnější k pohybovému aparátu.</p><p>Základní náplní našich tréninků je zdravotně preventivní příprava, výkonnostní příprava (rozvoj vyrtvalostních, rychlostních, silových, koordinačních schopností a pohyblivosti) a celkový rozvoj pohybových dovedností. využíváme cvičení na písku, díky kterému se zapojuje více svalových partíí a tím dochází ke komplexnějšímu zpevnění celého těla. Trénink je vhodný pro všechny věkové i výkonnostní kategorie. Přiďte si s námi zacvičit pro svou radost z pohybu.</p><h3>Kondiční tréninky</h3><p><span>Kondiční tréninky pořádáme nově každou neděli od 19 hodin. Trénink je vhodný jak pro mládež, tak pro dospělé včetně všech výkonnostních kategorií.</span></p><p></p><h3>Obsah a rozdělení jednotlivých tréninků:</h3><ul><li><p>Každou první neděli v měsíci bude trénink zaměřen na reakční rychlost a rychlost jako takovou. Při tréninku využijeme růžných odporových metod a reakčních cvičení. Od letní sezony i kontrastních cvičení pevný povrch-písek.</p></li><li>Každou druhou neděli v měsíci se zaměříme na tonizaci a posílení všech důležitých svalových skupin pomocí kruhového tréninku za použití různých pomůcek a zajímavých obměn již známých cviků.</li><li>Každou třetí neděli v měsíci dáme zabrat hlavně našim nohám při nácviku naší odrazové síly. Využívat budeme i různé překážky. Od léta opět zařadíme kontrastní cvičení mimo písek.</li><li>Každou čtvrtou neděli v měsíci se budeme soustředit na zpevěnění našeho posttoje a hlavně na posílení HSS (hlubokého stabilizačního systému). Cvičit budeme jak se svojí vahou, tak s pomůckami a využijeme i cvičení ve dvojicích.</li><li>Od letní sezony s přidáním tréninkových hodin i ve všední dny přidáme tréninky zaměřené na kompenzační cvičení pro volejbalisty, vytrvalostní trénink, různé posilovací tréninky s pomůckami i bez nich a tréninky na zlepšení celkové kondice. Při větším zájmu můžeme kterýkoliv trénink zařadit již nyní.</li></ul><h3>Náplň tréninkové jednotky:</h3><p><span><span>·<span>&nbsp;</span></span></span><span>Zahřátí</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Protažení</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Hlavní tréninková jednotka</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Závěrečné protažení</span></p><p>&nbsp;</p><h3>Cvičíme jak sami, tak ve dvojicích a při tréninku využíváme:</h3><ul><li><strong>Váhu vlastního těla</strong></li><li>Medicimbaly</li><li>TRX</li><li>Lavičky</li><li>Expandery</li><li>Aerobary</li></ul><h3><span><strong>Součástí kondičního tréninku jsou:</strong></span></h3><p><span><span>·<span>&nbsp;</span></span></span><span>Zdravotně preventivní příprava</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Kompenzační cvičení</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Výkonnostní příprava (zlepšení vytrvalostních, rychlostních, silových, odrazových, koordinačních schopností a pohyblivosti)</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Celkový rozvoj pohybových dovedností</span></p><h3><span><strong>Cíle kondičního tréninku jsou:</strong></span></h3><p><span><span>·<span>&nbsp;</span></span></span><span>Odstranění svalových disbalancí</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Zpevnění svalového tonusu</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Zlepšení celkové kondice</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Vytvarování postavy</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Redukce váhy na úkor tuků</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Nárůst svalové hmoty</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Zlepšení držení těla</span></p><p><span><span>·<span>&nbsp;</span></span></span><span>Zlepšení pohyblivosti</span></p><h3><span><strong>Délka tréninkové jednotky</strong></span></h3><p><span>90 minut</span></p><h3><span><strong>Cena tréninkové jednotky</strong></span></h3><p><span>1&nbsp;lekce 90 min. - 250 Kč. Do konce února zaváděcí sleva 20</span><span>%</span><span>&nbsp;na 200 Kč.&nbsp;<strong>První hodina je pro každého účastníka ZDARMA.</strong><br></span></p><p><span style=\"line-height: 1.42857143;\">Kondiční tréninky budeme vypisovat v našem rezervačním systému, kde se můžete on-line přihlašovat. Rezervační systém naleznete&nbsp;</span><a href=\"http://www.beach-ladvi.rezervuju.cz/training\" style=\"line-height: 1.42857143; background-color: rgb(255, 163, 54);\">zde</a><span style=\"line-height: 1.42857143;\">.</span><br></p>',	'<p><br></p>',	2,	0,	0,	'kondicni-treninky'),
(16,	'Tréninkové beach volejbalové turnaje v Praze',	'<p>Chcete natrénovat na beach volejbalový turnaj bez zbytečného stresu? Máte problém sehnat soupeře na stejné úrovni? Chcete si zahrát beach volejbal s novými protihráči, ale nechcete trávit celý den na turnaji? Pokud jste si zodpověděli alespoň jednou ano, tak právě pro vás jsme připravili tréninkové beachvolejbalové turnaje v Praze na Ládví.</p><h3>Turnaje v plážovém volejbale nanečisto</h3><p>Smyslem těchto turnajů je umožnit dvojicím se sehrát bez zbytečného stresu a bez klasických turnajových prodlev. V průběhu beach volejbalového turnaje nanečisto se nepočítají body ani skóre. Po odehrání setu se automaticky přesunete k dalšímu soupeři a hrajete dál. Nemusíte čekat ani se nervovat kvůli bodům.</p><h3>Páteční beach volejbalové párty v Praze pro mírně pokročilé</h3><p>Páteční beach volejbalové párty v Praze na Ládví jsou rezervované pro ty, kteří se chtějí odreagovat po náročném pracovním týdnu. Úroveň dvojic by se dala hodnotit jako mírně pokročilá, ale soupeře si zde najdou i dvojice pokročilé. Přijít můžete ve dvojice i sami. Garantujeme nepřetržitou hru po dobu 180 minut. Cena je 250 Kč, v případě permanentky 200 Kč.</p><p>Online rezervace<a href=\"http://www.beach-ladvi.rezervuju.cz/training\">&nbsp;zde.</a></p>',	'<p><br></p>',	2,	0,	0,	'treninkove-turnaje-praha'),
(17,	'Beachvolejbalové kempy v Bibione',	'<h3>Bibione - široké pláže, kvalitní služby a nízké ceny</h3><div><p><span>Populární středisko neustále zkvalitňuje své služby a&nbsp;infrastrukturu. Po&nbsp;právu proto patří k&nbsp;nejznámějším a&nbsp;nejoblíbenějším italským letoviskům vůbec, když v&nbsp;jeho prospěch &nbsp;hovoří i i&nbsp;malá vzdálenost z&nbsp;ČR. Bibione je ale také jednoznačným příkladem střediska, kterému předchází výrazně horší pověst, než je realita samotná. Častokrát je dokonce vnímáno jako synonymum nejlevnější masové turistiky začátku 90. let minulého století. Skutečnost dnešních dní je ale diametrálně odlišná. Ano, je pravdou, že lůžková kapacita je zde v&nbsp;řádu desítek tisíc lůžek, ale tomu odpovídá i&nbsp;velikost střediska, a&nbsp;již jen letmý pohled do&nbsp;ceníku naznačuje, že se nedá mluvit ani o&nbsp;letovisku nejlevnějším. I&nbsp;v&nbsp;tomto ohledu to je spíše naopak.</span></p></div><div><p><span>Nesčetné obchody včetně značkových butiků, široký výběr restaurací s&nbsp;typickou italskou i&nbsp;mezinárodní kuchyní, bary, herny, diskotéky, široké sportovní zázemí (namátkou lze jmenovat tenis, volejbal, fotbal, basketbal, lodičky, vodní lyže, windsurfing, vodní šlapadla, kajaky, rybaření, petanque, bowling, golf, minigolf, trampolíny, stolní tenis, jízdu na&nbsp;koni, půjčovny kol, kolečkových bruslí a&nbsp;šlapacích „čtyřkolek“, motokáry, aerobic, atd.), ale především bujarý večerní život s&nbsp;hlavní promenádou měnící se každodenně v&nbsp;korzující lidské mraveniště, to je ten typický obrázek letního Bibione. Nikdo z&nbsp;rekreantů si zpravidla nenechá ujít ani tradiční velké trhy konané každé úterý či návštěvu velkého a&nbsp;hlavně celosezónně otevřeného lunaparku. K&nbsp;návštěvě však láká i rozsáhlý vodní park Aquasplash a&nbsp;dětský pohádkový svět Gulliverlandia v&nbsp;jen 10 km vzdáleném Lignanu. To také již napovídá, že Bibione je ideálním místem pro děti. A&nbsp;je tomu skutečně tak. Vždyť jemný písek a&nbsp;pozvolný vstup do&nbsp;vody uchrání každého před starostmi o&nbsp;své ratolesti přes den a&nbsp;nepřeberné množství hřišť, dětských heren i&nbsp;další zábavy zase nabídne dokonalé rozptýlení na&nbsp;večer. Pláže jsou v&nbsp;Bibione až neskutečně dlouhé a&nbsp;široké.</span></p></div><div><p><span>V Bibione jsou též termální lázně celoročně vyhledávané pro ozdravné pobyty. Nabízejí řadu účinných léčebných kúr, když mezi nejžádanější patří zejména bahenní a&nbsp;vodní terapie či inhalační léčba dýchacích cest. Neopomeňme ale také Bibione jako příkladný prototyp letoviska, které Vás nenechá otálet ve&nbsp;dnech, kdy sluneční paprsky ještě nejsou tak intenzivně teplé či dokonce jsou schovány za&nbsp;nějakým tím mrakem. A&nbsp;to především díky výhodné poloze, která toto středisko doslova předurčuje k&nbsp;zajímavým výletům za&nbsp;poznáním. V&nbsp;pomyslném čele je dozajista nedaleká perla Jadranu - světoznámé Benátky, stejně snadno dostupný je ale i&nbsp;působivý přístav Terst, jehož dějiny jsou tak úzce svázány s&nbsp;těmi našimi, klenoty raného středověku Aquileia a&nbsp;Padova či Shakespearovým Romeem a&nbsp;Julií proslavená Verona. Neméně atraktivní jsou i&nbsp;výlety lodí po&nbsp;lagunách v&nbsp;oblasti mezi Bibione a&nbsp;Caorle nebo o&nbsp;něco delší plavba okolo benátských ostrovů.</span></p></div><p><span style=\"font-family: BenchNine, sans-serif; font-size: 20px; line-height: 26px;\">Ubytování týmu Beachklubu Ládví</span><br></p><h3>Villaggio Michelangelo a San Siro***</h3><h4>Poloha / Pláž</h4><p>Bibione - centrum, pláž - 550 - 600 m</p><h4>Upřesňující popis</h4><p>Rozsáhlý komplex dvoupodlažních řadových vilek s několika bazény (Michelangelo) a sousední menší komplex jednopodlažních a dvoupodlažních řadových vilek s jedním bazénem (San Siro).</p><h4>Vybavenost a služby</h4><p>dětské hřiště (jen Michelangelo), 1 vyhrazené venkovní parkovací stání / apartmán (další parkování v blízkosti)</p><h4>Sport a relaxace</h4><p>několik různě velkých bazénů a dětských bazénků</p><h4>Vybavenost apartmánů</h4><p>zpravidla TV, trezor, u typologií quadrilo a trilo zpravidla též venkovní krb</p><h4>Popis apartmánů</h4><p>mono 4 - 25 m2 - obývací pokoj s kuchyňským koutem a 2 rozkládacími gauči (možno i typ „šuplík“) pro 2 osoby či 1 rozkládacím gaučem (možno i typ „šuplík“) a palandou, sociální zařízení se sprchou, předzahrádka; apartmány jsou výhradně ve Villaggiu San Siro</p><p>bilo 3/4/5 - 35 m2 - 1 ložnice s manželskou postelí a&nbsp;<br>případně 1 samostatným lůžkem, obývací pokoj s kuchyňským koutem a rozkládacím gaučem pro 1 či 2 osoby (možno i typ „šuplík“), sociální zařízení se sprchou, balkon či předzahrádka případně s terasou<br><br>trilo 5/6/7 - 45 až 55 m2 - 1 ložnice s manželskou postelí, 1 ložnice se 2 samostatnými lůžky či 1 samostatným lůžkem a palandou délky 180 cm, obývací pokoj s kuchyňským koutem a rozkládacím gaučem pro 1 či 2 osoby (možno i typ „šuplík“), sociální zařízení se sprchou, balkon či terasa a předzahrádka;&nbsp;<br><br>některé apartmány jsou dvoupodlažní, když ve 2. podlaží jsou obě ložnice a sociální zařízení</p><h4>Důležitá upozornění</h4><p>bazény v rámci jednotlivých ubytovacích kapacit jsou v provozu cca od 15.05. do 15.09.</p><h4>VÝHODY A NEVÝHODY</h4><ul><li>příjemné, velmi oblíbené a v posledních letech i vůbec nejžádanější konkrétní ubytování v Bibione</li><li>dostatek klidu, soukromí i zeleně</li><li>ideální poloha vzhledem k rušnému centru</li><li>nabídka nejžádanějších typologií</li><li>dostatečná kapacita i pro skupinové zájezdy</li><li>u některých apartmánů starší vybavení a zařízení</li></ul>',	'<p><br></p>',	2,	0,	0,	'kempy-v-bibione'),
(18,	'Beach volleyball in Prague',	'<h3>About Beachklub Ládví<br></h3><p>Beachklub Ládví is the biggest beach volleyball centre in Prague. We started in 2008 by building first 6 pitches. We were lucky and got the chance to buy all the sand from the first FIVB World Tour tournament in Prague. Nowadays there are 15 beach volleyball courts open. You would not believe that we have already transported around 3.750 tuns of sand. Naturally all of them from the past a few World Tour tournament in Prague :). It means we are not only the biggest, but we have also the best sand.</p><h3>Book a beach volleybal court<br></h3><p>We have altogether 15 beach volleyball pitches. You can book them online&nbsp;<a href=\"https://jdemenato.cz/reservation/beachklub-ladvi/reservationcalendaroverview\">here.</a></p><h3>Beach volleyball trainings</h3><p>We have several experienced beach volleyball&nbsp;<a href=\"/o-nas\">coaches</a>. Ondřej played the first division in Austria and in Cyprus. Jarda even trained the Austrian U19 national team and played also first division abroad (Portugal, Austria).</p><div><p>We have beach volleyball trainings for all the age categories. Starting with the small children from 3 years and ending by 50+ groups. You can either join the group during the beachcamps or have the private, individual lessons.</p><h3>Beach volleyball tournaments</h3><p>Nearly every weekend we organize beach volleyball tournaments for amateures, kids and sometimes even for the profi players. Check&nbsp;<a href=\"/turnaje\">the tournament list</a>&nbsp;to find out the dates and categories.</p><h3>Beachbar</h3><p></p><p>What would&nbsp;be&nbsp;a beachvolleyball sport club without a beachbar. You can rely on us. We serve nice food (steaks, soups, burgers and some Czech Specials like hermelín or párek v rohlíku). We have draft beer (12´, 10´. light) and all kinds of soft drinks.</p><p><br>Just come and enjoy.</p></div>',	'<p><br></p>',	2,	0,	0,	'beach-volleyball-in-prague');
