INSERT INTO `partners` (`id`, `name`, `desc`, `ord`, `url`, `gallery_picture_id`, `partner_type_id`) VALUES
(1,	'Hostel Praha Ládví',	'<p>Hostel Praha Ládví svým zákazníků nabízí levné ubytování s možností parkování a výbornou dostupností do centra města.</p>',	2,	'http://hostelprahaladvi.cz',	61,	1),
(2,	'Wilson',	'<p><span style=\"line-height: 1.42857143;\">České zastoupení společnosti AMER SPORTS Czech Republic, s.r.o. zodpovídá za distribuci a prodej značek Atomic, Salomon, Suunto, Dynamic, Volant, Nikita, Bonfire, Salomon Snowboards a Wilson.</span></p>',	0,	'http://www.amersports.cz/wilson',	66,	1),
(3,	'Aquacentrum Šutka',	'<p><span style=\"line-height: 1.42857143;\">Aquacentrum Šutka je jedním z nejmodernějších plaveckých areálů v Praze i celé České republice.</span> Svým návštěvníkům nabízí nejen padesátimetrový krytý bazén, ale též vířivku, divokou řeku, 2 tobogány, vodopád, relaxační bazén, dětské brouzdaliště i vodní bar s občerstvením. Pro milovníky wellness jsou v areálu k dispozici dvě finské sauny s venkovními ochlazovacími bazénky a dvě parní lázně.</p>',	6,	'http://www.sutka.eu/',	71,	3),
(4,	'NEUMANN',	'<p>NEUMANN, spol. s r.o. je česká rodinná firma specializující se na výrobu střešních boxů a střešních nosičů. Společnost patří k největším výrobcům i prodejcům uvedeného druhu výrobků v České republice.&nbsp;</p>',	7,	'http://www.neumannr.cz/cs/',	76,	3),
(5,	'Ferdinand',	'<p>Pivovar vyrábí&nbsp;okolo&nbsp;25 000 hektolitrů piva ročně. Produkuje celkem&nbsp;sedm druhů piv od nealkoholického až po speciální piva. Obchodní politika pivovaru se zaměřuje především domácí region a dále na exportní trhy.</p><p><br></p>',	3,	'http://www.pivovarferdinand.cz/',	81,	1),
(6,	'Monstera International',	'<p>Společnost Monstera je developerskou organizací zabývající se investiční činností v oblasti komerčních nemovitostí a se zaměřením na výstavbu čerpacích stanic pohonných hmot.</p>',	8,	'http://www.monstera.cz/',	86,	3),
(7,	'Hlavní město Praha',	'<p><span>Magistrát hlavního města Prahy je fakticky obecním a krajským úřadem hlavního města Prahy. Jeho působnost je vymezena zákonem o hlavním městě Praze a městkou vyhláškou o Statutu&nbsp;</span>hlavního města Prahy, činnost a organizační uspořádání magistrátu upravuje jeho organizační řád. Magistrát plní úkoly uložené zastupitelstvem nebo radou města a vykonává přenesenou státní působnost hlavního města.<br></p>',	4,	'http://www.praha.eu/',	91,	1),
(8,	'Městská část Praha 8',	'<p>Městský obvod&nbsp;Praha 8<span> je územní samosprávný celek zabývající se všeobecnou veřejnou správou.&nbsp;</span><br></p>',	5,	'http://www.praha8.cz/',	101,	1),
(11,	'Decathlon',	'<p>Decathlon je významný maloobchodní prodejce sportovního vybavení, oblečení a obuvi. Ve svých prodejnách v 19 zemích světa nabízí sortiment až pro 70 druhů sportů!<br></p>',	1,	'http://www.decathlon.cz/',	362,	1),
(12,	'Prima',	'<p>Společnost Bidvest vyvíjí, vyrábí a dodává zmrzlinu Prima do obchodů, cukráren a restaurací. Ve vlastním moderním závodě v Opavě vyrobí 12 miliónů litrů zmrzliny za rok. Mezi ně patří oblíbené nanuky Mrož a Pegas, Ruská zmrzlina, zmrzlinové dorty, zmrzlinové vany a další zmrzlinové výrobky.<br></p>',	9999999,	'http://www.primazmrzlina.cz/',	368,	3);
